mapper.insert:
INSERT INTO CURATION(
CURATION_CODE
, CURATION_AGE
, CURATION_INGRA
, CURATION_DISEASE
, CURATION_ALLERGY
, CURATION_BREED
, CURATION_GENDER
, CURATION_NEUT
, CURATION_WEIGHT
, CURATION_NAME
, CURATION_DATE
, CURATION_SIZE
, CURATION_COOK
, USER_CODE)
VALUES(
#{ curationCode }
, #{ curationAge }
, #{ curationIngra }
, #{ curationDisease }
, #{ curationAllergy }
, #{ curationBreed }
, #{ curationGender }
, #{ curationNeut }
, #{ curationWeight }
, #{ curationName }
, #{ curationDate }
, #{ curationSize }
, #{ curationCook }
, #{ userCode })

mapper.select:
SELECT
    CURATION_CODE
    , CURATION_AGE
    , CURATION_INGRA
    , CURATION_DISEASE
    , CURATION_ALLERGY
    , CURATION_BREED
    , CURATION_GENDER
    , CURATION_NEUT
    , CURATION_WEIGHT
    , CURATION_NAME
    , CURATION_DATE
    , CURATION_SIZE
    , CURATION_COOK
    , USER_CODE
FROM CURATION
WHERE
    CURATION_AGE = #{ curationAge }
    AND CURATION_INGRA = #{ curationIngra }
    AND CURATION_DISEASE = #{ curationDisease }
    AND CURATION_ALLERGY = #{ curationAllergy }
    AND CURATION_BREED = #{ curationBreed }
    AND CURATION_GENDER = #{ curationGender }
    AND CURATION_NEUT = #{ curationNeut }
    AND CURATION_WEIGHT = #{ curationWeight }
    AND CURATION_NAME = #{ curationName }
    AND CURATION_DATE = #{ parsedDate }
    AND CURATION_SIZE = #{ curationSize }
    AND CURATION_COOK = #{ curationCook }
    AND USER_CODE = #{ userCode }
