mapper.insert:
INSERT INTO PROD(
PROD_CODE
, PROD_NAME
, PROD_MANUFAC
, PROD_PRICE
, PROD_AGE
, PROD_EFFI
, PROD_RECOM
, PROD_SITE
, PROD_COOK
, PROD_VOLUME
, PROD_GRADE
, PROD_INGRA
, PROD_SIZE
, PROD_DATE
, PROD_STATUS
, PROD_IMAGE
, PROD_VISIT)
VALUES(
#{ prodCode }
, #{ prodName }
, #{ prodManufac }
, #{ prodPrice }
, #{ prodAge }
, #{ prodEffi }
, #{ prodRecom }
, #{ prodSite }
, #{ prodCook }
, #{ prodVolume }
, #{ prodGrade }
, #{ prodIngra }
, #{ prodSize }
, CURDATE()
, 'Y'
, #{ prodImage }
, 0)

mapper.update:
UPDATE PROD

    PROD_CODE = #{ prodCode}

        , PROD_NAME = #{ prodName }


        , PROD_MANUFAC = #{ prodManufac }


        , PROD_PRICE = #{ prodPrice }


        , PROD_AGE = #{ prodAge }


        , PROD_EFFI = #{ prodEffi }


        , PROD_RECOM = #{ prodRecom }


        , PROD_SITE = #{ prodSite }


        , PROD_COOK = #{ prodCook }


        , PROD_VOLUME = #{ prodVolume }


        , PROD_GRADE = #{ prodGrade }


        , PROD_INGRA = #{ prodIngra }


        , PROD_SIZE = #{ prodSize }


        , PROD_STATUS = #{ prodStatus }


        , PROD_IMAGE = #{ prodImage }


        , PROD_VISIT = #{ prodVisit }


WHERE PROD_CODE = #{ prodCode }
