mapper.select:
SELECT *
FROM USER
WHERE USER_ID = #{ userId }

mapper.insert:
INSERT INTO USER
(
USER_ID
, USER_PW
, USER_NICK
, USER_PHONE
, USER_SIGNUP
, USER_LATEST
, USER_AUTH
)
VALUES
(
#{ userId }
, #{ userPass }
, #{ userNick }
, #{ userPhone }
, now()
, now()
, 'USER'
)

